import React, { Component } from 'react';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import FormInput from '../FormInput/FormInput';
import {
  LoansWrap,
  LoansItem,
  SubmitAction,
  SubmitButton,
} from './LoansComponent.style';

const validationSchema = Yup.object().shape({
  identificationNumber: Yup.number().required('Personal Identification number is required'),
  dateOfBirth: Yup.string().trim(),
  country: Yup.string().trim().required('The country is required'),
  phoneNumber: Yup.number().required('The phone number is Required'),
});

const initialValues = {
  identificationNumber: '',
  dateOfBirth: '',
  country: '',
  phoneNumber: '',
};

class LoansComponent extends Component {
  loanSubmit = (values) => {
    const { loanSubmit } = this.props;
    loanSubmit(values);
  }

  renderSubmitBtn = () => {
    const { isLoading } = this.props;
    return (
      isLoading
        ? <SubmitButton>Loading ...</SubmitButton>
        : <SubmitButton type="submit">Submit</SubmitButton>
    );
  }

  render() {
    return (
      <LoansWrap>
        <LoansItem>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={this.loanSubmit}
          >
            <Form>
              <FormInput
                type="text"
                name="identityNumber"
                label="Personal Identification Number"
              />
              <FormInput
                type="date"
                name="dateOfBirth"
                label="Date Of Birth"
              />
              <FormInput
                type="select"
                name="country"
                label="Country"
              />
              <FormInput
                type="text"
                name="phoneNumber"
                label="Phone Number"
              />
              <SubmitAction>
                {this.renderSubmitBtn()}
              </SubmitAction>
            </Form>
          </Formik>
        </LoansItem>
      </LoansWrap>
    );
  }
}

export default LoansComponent;
