/* eslint-disable eqeqeq */
/* eslint-disable no-nested-ternary */
import styled, { css } from 'styled-components';

export const TableHeadWrapper = styled.div`
  display: flex;
  border: none;
  color: ${props => props.theme.colorStyled.ColorPrimary};
  background-color: ${props => props.theme.colorStyled.ColorWhite};
  border-radius: .5em;
  margin-bottom: 1em;
  font-weight: 700;
  line-height: 1.4;
  min-height: 35px;
  padding: .5em 1em;

  ${({ border }) => border && css`
    border: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
  `}
  ${({ noBackground }) => noBackground && css`
    background-color: transparent;
    border: none;
    padding: 0em 1em;
  `}

  ${({ noPadding }) => noPadding && css`
    padding: 0px;
  `}
  ${({ smallSize }) => smallSize && css`
    min-height: 3em;
    padding: 0em;
    border: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
  `}
`;

export const TableContainerWrap = styled.div`
  width: 100%;
`;

export const TableContentGroup = styled.div`
  p {
    text-transform: capitalize;
    font-family: 'MontserratSemiBold';
  }
  span {
    text-transform: lowercase;
    font-family: 'MontserratMedium';
    color: ${props => props.theme.colorStyled.ColorHeaderTable};
  }
`;

export const TableContentWrapper = styled.div`
  border-top: none;
  color: #263035;
  font-weight: 700;
  line-height: 1.4;
  padding: .5em 1em;
  border-radius: .5em;
  background-color: ${props => props.theme.colorStyled.ColorWhite};
  ${({ border }) => border
    && css`
      border: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
      padding: 0em;
      overflow: hidden;
  `};
`;

export const TableContentItem = styled.div`
  display: flex;
  flex: 1;
  border: none;
  min-height: 3em;
  background-color: ${props => props.theme.colorStyled.ColorWhite};
  &:hover {
    background-color: #f7f7f7 !important;
    border-radius: .325em;
  }
  ${({ smallSize }) => smallSize && css`
    min-height: 3em;
    border-bottom: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
    &:last-child {
      border-bottom: none;
    }
  `}
  ${({ custome }) => custome && css`
    padding: .5em 1em;
    margin-bottom: .5em;
    border-radius: .5em;
    box-shadow: 0px 0px 6px -1px ${props => props.theme.colorStyled.ColorBoxShadow};
  `}
  ${({ noHover }) => noHover && css`
    &:hover {
      background-color: transparent !important;
    }
  `}
`;

export const TableHead = styled.div`
  flex: 1;
  padding: 0 5px;
  padding: 8px;
  display: flex;
  align-items: center;
  text-transform: capitalize;
  transition: all 300ms ease;
  color: ${props => props.theme.colorStyled.ColorHeaderTable};
  ${({ size }) => size
    && css`
      flex: 0 0 ${size}px;
      @media (max-width: 480px) {
        flex: 0 0 60px;
      }
    `};
  ${({ percent }) => percent
    && css`
      flex: 0 0 ${percent}%;
    `};
  ${({ textCenter }) => textCenter
    && css`
      justify-content: center;
      text-align: center;
    `};
  ${({ textRight }) => textRight
    && css`
      justify-content: flex-end;
    `};
  ${({ borderRight }) => borderRight
    && css`
      border-right: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
      &:last-child {
        border-right: none;
      }
    `};
  ${({ borderLeft }) => borderLeft
    && css`
      border-left: 1px solid ${props => props.theme.colorStyled.ColorBorderInput};
    `};
  ${({ smallSize }) => smallSize && css`
    font-size: ${props => props.theme.fontSize.PrimaryFontSize};
    padding: .5em;
  `};
  ${({ thinStyle }) => thinStyle && css`
    font-family: 'MontserratRegular';
  `};
  ${({ boldStyle }) => boldStyle && css`
    font-family: 'MontserratBold';
  `};
  ${({ isActive }) => isActive && css`
    color: #4886FE;
  `};
  ${({ paddingLarge }) => paddingLarge && css`
    padding: .5em 1em;
  `}
  ${({ shouldRender }) => shouldRender && css`
    @media (max-width: 768px) {
      display: none;
    }
  `}
  a {
    color: #586069;
    &:hover {
      color: #ff5402;
    }
  }
  .chalktalk-up-down-arrow {
    cursor: pointer;
    margin-left: .5em;
    font-size: ${props => props.theme.fontSize.MediumFontSize};
    &:hover {
      opacity: 0.8;
    }
  }

  ${({ borderBottom }) => borderBottom && css`
    border-bottom: 1px solid #091e4214;
  `};
  ${({ haveResponsive }) => haveResponsive && css`
    @media (max-width: 1170px) {
      flex: 1;
      max-width: 20em;
    }
    @media (max-width: 768px) {
      max-width: 15em;
    }
  `}
`;

export const TableRow = styled(TableHead)`
  color: ${props => props.theme.colorStyled.ColorPrimary};
  font-style: ${props => (props.italic ? 'italic' : 'initial')};
  .icon-dots {
    font-size: 10px;
    color: ${props => props.theme.colorStyled.ColorBorderInput};
  }
  ${({ input }) => input
    && css`
      input {
        width: 100%;
        text-align: center;
        padding: 5px 0px;
        min-height: 30px;
        &:focus {
          border-radius: 3px;
