import styled, { css, keyframes } from 'styled-components';

const formElementCss = css`
  border: none;
  border-bottom: 1px solid #dbdbdb;
  color: #333;
  position: relative;
  outline: none;
  width: 100%;
  font-size: ${props => props.theme.fontSize.DefaultFontSize};
  padding: 5px 10px;
  height: 38px;
  transition: border 0.3s;
  background: #fff;
  border-radius: 3px;
  line-height: 1.8;
  z-index: 2;
  font-family: 'MontserratMedium';
  &:focus {
    outline: none;
    border-bottom: 1.5px solid ${props => props.theme.colorStyled.ColorBgDefault};
  }
`;

const BounceInput = keyframes`
 0% {
    transform: translateX(0px);
    timing-function: ease-in;
  }
  37% {
    transform: translateX(14px);
    timing-function: ease-out;
  }
  55% {
    transform: translateX(-14px);
    timing-function: ease-in;
  }
  73% {
    transform: translateX(14px);
    timing-function: ease-out;
  }
  82% {
    transform: translateX(-14px);
    timing-function: ease-in;
  }
  91% {
    transform: translateX(14px);
    timing-function: ease-out;
  }
  96% {
    transform: translateX(-14px);
    timing-function: ease-in;
  }
  100% {
    transform: translateX(0px);
    timing-function: ease-in;
  }
`;

export const FormInputStyled = styled.input.attrs({
  className: 'input-text',
})`
  ${formElementCss};
  cursor: text;

  ${({ isInInviteForm }) => isInInviteForm && css`
    padding: 5px 0px;
    z-index: 0;
  `}
`;

export const FormCheckboxStyled = styled.label`
  margin-bottom: 5px;
  margin: 5px 0px 10px;
  display: flex;
  align-items: center;
  position: relative;
  input {
    margin-right: 10px;
  }
  span {
    font-size: ${props => props.theme.fontSize.DefaultFontSize};;
    margin-left: 25px;
  }
  & > .checkmark {
    position: absolute;
    top: 0px;
    left: -25px;
    height: 15px;
    width: 15px;
    background-color: #fff;
    border-radius: 3px;
    border: 1px solid #d3d3d3;
    &:after {
      content: '';
      position: absolute;
      display: none;
      width: 100%;
      height: 100%;
    }
  }
  & > input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    &:checked {
      & ~ .checkmark {
        height: 15px;
        width: 15px;
        border-color: #ff9e16;
        &:after {
          display: block;
          background-color: #ff9e16;
        }
      }
    }
    &:hover {
      & > input {
        & ~ .checkmark {
          background-color: #ccc;
        }
      }
    }
  }
`;

export const FormSelectStyled = styled.select.attrs({
  className: 'input-select',
})`
  ${formElementCss};
`;

export const FormTextAreaStyled = styled.textarea.attrs({
  className: 'text-area',
})`
  ${formElementCss};
  height: unset;
`;

export const FormInputErrorMessage = styled.div`
  color: #ad4444;
  width: 100%;
  font-size: ${props => props.theme.fontSize.DefaultFontSize};;
  text-align: left;
  margin-top: 5px;
`;

export const FormInputWrapperStyled = styled.div`
  margin-bottom: 10px;
  width: 100%;
  position: relative;
  background: transparent;
  .select__option {
    border-color: #dbdbdb;
    cursor: pointer;
    display: flex;
    align-items: center;
    padding: 8px 12px;
    font-size: ${props => props.theme.fontSize.DefaultFontSize};;
  }
  .select__menu {
    z-index: 3;
  }
  .select__control {
    cursor: pointer;
    border: none;
    border-radius: 0px;
    border-bottom: 1px solid #dbdbdb;
    padding: 5px 10px;
    min-height: 28px;
    &:hover {
      border-color: #dbdbdb;
    }
  }
  .select__value-container {
    min-width: 150px;
    font-size: ${props => props.theme.fontSize.DefaultFontSize};;
    padding: 0px;
  }
  .select__control--is-focused {
    box-shadow: none;
    border-bottom: 1.5px solid ${props => props.theme.colorStyled.ColorBgDefault}; !important;
    .select__indicator {
      color: ${props => props.theme.colorStyled.ColorBgDefault}; !important;
    }
  }
