import React, { Component } from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import FormInput from '../FormInput/FormInput';
import { countryData } from './CountryData';
import {
  LoansWrap,
  LoansItem,
  SubmitAction,
  SubmitButton,
} from './LoansComponent.style';

const validationSchema = Yup.object().shape({
  identificationNumber: Yup.string().trim().required('Personal Identification number is required'),
  dateOfBirth: Yup.string().trim(),
  country: Yup.string().trim().required('The country is required'),
  phoneNumber: Yup.string().trim().required('The phone number is Required'),
});

const initialValues = {
  identificationNumber: '',
  dateOfBirth: '',
  country: '',
  phoneNumber: '',
};

class LoanForm extends Component {
  loanSubmit = (values) => {
    const { loanSubmit } = this.props;
    loanSubmit(values);
  }

  renderSubmitBtn = () => {
    const { isLoading } = this.props;
    return (
      isLoading
        ? <SubmitButton>Loading ...</SubmitButton>
        : <SubmitButton type="submit">Submit</SubmitButton>
    );
  }

  renderAmountField = (fieldProps) => {
    const { field, label } = fieldProps;
    const { name, value } = field;

    return (
      <FormInput
        type="text"
        name={name}
        value={`${value} USD`}
        label={label}
      />
    );
  }

  render() {
    return (
      <LoansWrap>
        <LoansItem>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={this.loanSubmit}
          >
            <Form>
              <FormInput
                type="text"
                name="identityNumber"
                label="Personal Identification Number"
              />
              <FormInput
                type="date"
                name="dateOfBirth"
                label="Date Of Birth"
              />
              <FormInput
                type="select"
                name="country"
                label="Country"
                options={countryData}
              />
              <FormInput
                type="number"
                name="phoneNumber"
                label="Phone Number"
              />
               <FormInput
                type="number"
                name="amount"
                label="Amount"
              />
              <Field
                component={this.renderAmountField}
                name="amount"
                label="Amount (USD Only)"
              />
              <FormInput
                type="date"
                name="loanTerm"
                label="Loan Term"
                minDate={new Date()}
              />
              <SubmitAction>
                {this.renderSubmitBtn()}
              </SubmitAction>
            </Form>
          </Formik>
        </LoansItem>
      </LoansWrap>
    );
  }
}

export default LoanForm;
