import React, { Component } from 'react';
import moment from 'moment';
import {
  LoanApproveWrap,
  LoanNoDataMessage,
  LoanApproveItemWrap,
  LoanApproveContent,
} from './LoanApprove.style';

class LoanApprove extends Component {
  renderLoanListApprove = (values, index) => {
    console.log('values', values);
    const { country } = values;

    return (
      <LoanApproveItemWrap key={index}>
        <LoanApproveContent>
          <span>Identification Number: </span>
          <div>{values.identificationNumber}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Date of birth: </span>
          <div>{moment(values.dateOfBirth).format("YYYY/MM/DD")}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Country: </span>
          <div>{country.value}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Phone Number: </span>
          <div>{values.phoneNumber}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Amount: </span>
          <div>{values.amount}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Term: </span>
          <div>{moment(values.loanTerm).fromNow()}</div>
        </LoanApproveContent>
        <LoanApproveContent>
          <span>Loan Date: </span>
          <div>{moment(values.loanDate).format("YYYY/MM/DD h:mm a")}</div>
        </LoanApproveContent>
      </LoanApproveItemWrap>
    )
  }

  renderNoDataMessage = () => (
    <LoanNoDataMessage>
      Empty Loan
    </LoanNoDataMessage>
  )

  render() {
    const { loanList } = this.props;
    if (loanList.length < 0) return this.renderNoDataMessage();

    return (
      <LoanApproveWrap>
        {loanList.map(this.renderLoanListApprove)}
      </LoanApproveWrap>
    );
  }
}

export default LoanApprove;
