import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import LoanForm from '../../components/LoanForm';
import { 
  actions as loanAction,
  selectors as loanSelectors,
} from '../../reducers/loans';

const mapStateToProps = (state) => ({
  isLoanSuccess: loanSelectors.getLoadSuccess(state),
});

const mapDispatchToProps = {
  loanSubmit: loanAction.loanSubmit,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(LoanForm));
